// Definitions for RS pin #define DisplayCmdDataConfig() TRISAbits.TRISA1 = 0 #define DisplaySetCommand() LATAbits.LATA1 = 0 #define DisplaySetData() LATAbits.LATA1 = 1
// Definitions for CS pin #define DisplayConfig() TRISBbits.TRISB15 = 0 #define DisplayEnable() LATBbits.LATB15 = 0 #define DisplayDisable() LATBbits.LATB15 = 1
// Definitions for FLASH CS pin #define DisplayFlashConfig() #define DisplayFlashEnable() #define DisplayFlashDisable()
// Definitions for POWER ON pin #define DisplayPowerConfig() #define DisplayPowerOn() #define DisplayPowerOff()
// Definitions for backlight control pin #define DisplayBacklightConfig() #define DisplayBacklightOn() #define DisplayBacklightOff()
#elif defined (__PIC32MX__) // When using the supported PIC32 devices
