// Definitions for reset pin #define DisplayResetConfig() TRISACLR = _TRISA_TRISA4_MASK #define DisplayResetEnable() LATACLR = _LATA_LATA4_MASK #define DisplayResetDisable() LATASET = _LATA_LATA4_MASK
