// Definitions for backlight control pin #define DisplayBacklightConfig() #define DisplayBacklightOn() #define DisplayBacklightOff() #else // Definitions for reset pin #define DisplayResetConfig() TRISCCLR = _TRISC_TRISC1_MASK #define DisplayResetEnable() LATCCLR = _LATC_LATC1_MASK #define DisplayResetDisable() LATCSET = _LATC_LATC1_MASK
