// Definitions for CS pin #define DisplayConfig() TRISDCLR = _TRISD_TRISD10_MASK #define DisplayEnable() LATDCLR = _LATD_LATD10_MASK #define DisplayDisable() LATDSET = _LATD_LATD10_MASK
// Definitions for backlight control pin #define DisplayBacklightConfig() (TRISDbits.TRISD0 = 0) #define DisplayBacklightOn() (LATDbits.LATD0 = BACKLIGHT_ENABLE_LEVEL) #define DisplayBacklightOff() (LATDbits.LATD0 = BACKLIGHT_DISABLE_LEVEL) #endif // Definitions for FLASH CS pin #define DisplayFlashConfig() #define DisplayFlashEnable() #define DisplayFlashDisable()
